mapper.select:
select A.CMC_CODE , A.CMC_PARENT_CODE , A.CMC_TYPE , A.CMC_USE_YN , A.CMC_REGDATE , B.CNM_NAME , B.CNM_DESCRIPTION , B.CNM_LANG_CODE
from DNKR_COMMON_CODE A, DNKR_COMMON_CODE_NM B where 'SqlMap-Code.searchByKey' = 'SqlMap-Code.searchByKey' and A.CMC_CODE = B.CMC_CODE and A.CMC_CODE = #{cmcCode}
and B.CNM_LANG_CODE = #{cnmLangCode}
and B.CNM_LANG_CODE = 'en'

select A.CMC_CODE , A.CMC_PARENT_CODE , A.CMC_TYPE , A.CMC_USE_YN , A.CMC_REGDATE , B.CNM_NAME , B.CNM_DESCRIPTION , B.CNM_LANG_CODE from DNKR_COMMON_CODE A left outer join (select CMC_CODE , CNM_NAME , CNM_DESCRIPTION , CNM_LANG_CODE from DNKR_COMMON_CODE_NM where 'SqlMap-Code.searchByCondition' = 'SqlMap-Code.searchByCondition'
and CNM_LANG_CODE = #{cnmLangCode}
and CNM_LANG_CODE = 'en'
) B on A.CMC_CODE = B.CMC_CODE where 'SqlMap-Code.searchByCondition' = 'SqlMap-Code.searchByCondition'
and A.CMC_CODE = #{cmcCode}
and A.CMC_PARENT_CODE = #{cmcParentCode}
and A.CMC_TYPE = #{cmcType}
and A.CMC_USE_YN = #{cmcUseYN}
and B.CNM_NAME Like '%' || #{cnmName} || '%'
and A.CMC_REGDATE = #{cmcRegdate}
order by A.CMC_CODE asc

select 
        count(rownum)
from 
        DNKR_COMMON_CODE
where 
        'SqlMap-Code.searchCount' = 'SqlMap-Code.searchCount'

    and CMC_CODE = #{cmcCode}


    and CMC_PARENT_CODE = #{cmcParentCode}


    and CMC_TYPE = #{cmcType}


    and CMC_USE_YN = #{cmcUseYN}


    and CNM_LANG_CODE = #{cnmLangCode}


    and CMC_REGDATE = #{cmcRegdate}

select 
        count(rownum)
from 
        DNKR_COMMON_CODE
where 
        'SqlMap-Code.searchCount' = 'SqlMap-Code.searchCount'

    and CMC_CODE = #{cmcCode}


    and CMC_PARENT_CODE = #{cmcParentCode}


    and CMC_TYPE = #{cmcType}


    and CMC_USE_YN = #{cmcUseYN}


    and CNM_LANG_CODE = #{cnmLangCode}


    and CMC_REGDATE = #{cmcRegdate}

mapper.insert:
insert into DNKR_COMMON_CODE_NM (
        /* SqlMap-Code.createCnm */ 

        CMC_CODE,


        CNM_NAME,


        CNM_DESCRIPTION,


        CNM_LANG_CODE

) values (

        #{cmcCode},


        #{cnmName},


        #{cnmDescription},


        #{cnmLangCode}

)

mapper.update:
update DNKR_COMMON_CODE set
CMC_USE_YN = #{cmcUseYN}
where 'SqlMap-Code.updateCnm' = 'SqlMap-Code.updateCnm' and CMC_CODE = #{cmcCode}

update 
        DNKR_COMMON_CODE_NM 
set

        CNM_NAME = #{cnmName},


        CNM_DESCRIPTION = #{cnmDescription}


        CNM_DESCRIPTION = ''

where 
        'SqlMap-Code.updateCnm' = 'SqlMap-Code.updateCnm'
    and CMC_CODE = #{cmcCode}
    and CNM_LANG_CODE = #{cnmLangCode}

mapper.delete:
delete from 
        DNKR_COMMON_CODE_NM
where 
        'SqlMap-Code.deleteCnm' = 'SqlMap-Code.deleteCnm'

    and CMC_CODE = #{cmcCode}


    and CNM_LANG_CODE = #{cnmLangCode}
